import React, { useState } from 'react'

const closing = (opt) => {
  // alert("I am Clicked");
  if (opt === false) {
    document.getElementById("tForm").style.display = "block";
    document.getElementById("tSearch").style.display = "none";
  } else {
    document.getElementById("tForm").style.display = "none";
    document.getElementById("tSearch").style.display = "block";
  }
}

const closingView = () => {
  // alert("I am Clicked");

}


const Student = () => {
  // const [teachers, setTeachers] = useState([]);
  const [Name, setName] = useState("");
  const [Surname, setSurname] = useState("");
  const [Edu, setEdu] = useState("");
  const [Stream, setStream] = useState("");
  const [Comm, setComm] = useState("");
  const [Add, setAdd] = useState("");
  const [Adno, setAdno] = useState("");
  const [Ref, setRef] = useState("");
  const [Mob, setMob] = useState("");

  // Submitting The Form

  const submit = async (e) => {
    e.preventDefault();
    // alert("Hi " + Name + Surname + Edu + Stream + Comm + Add + Adno + Ref + Mob)

    const formData = {
      Name,
      Surname,
      Edu,
      Stream,
      Comm,
      Add,
      Adno,
      Ref,
      Mob
    };

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      });
      console.log(response);
      alert("Form submitted successfully!");
    }
    catch (err) {
      console.log(err)
      alert("Error submitting form");
    }

  }

  // Getting Data from Database
  let teachers;
  const getData = async () => {
    try {
      // const res = await fetch("http://localhost:5000/teachers");
      // const data = await res.json();
      // setTeachers(data);
    } catch (error) {
      console.error("Error fetching teachers", error.status);
    }
    teachers = [
      {
        "Id": 1,
        "Name": "Talha",
        "Surname": "Ansari",
        "Education": "Graduation",
        "Stream": "IT",
        "Comm": "2.5",
        "Address": "Hafiz Nagar, Bhiwandi",
        "Adno": 564656,
        "Reference": "Banner",
        "Mobile_number": 9881375611
      },
      {
        "Id": 2,
        "Name": "Talha",
        "Surname": "Ansari",
        "Education": "Graduation",
        "Stream": "IT",
        "Comm": "2.5",
        "Address": "Hafiz Nagar, Bhiwandi",
        "Adno": 564656,
        "Reference": "Banner",
        "Mobile_number": 9881375611
      }
    ];
  }
  getData();

  const closingView = (opt) => {
    // alert("I am Clicked");
    if (opt === true) {
      document.getElementById("tView").style.display = "none";
      document.getElementById("tSearch").style.display = "block";
    } else {
      document.getElementById("tView").style.display = "block";
      document.getElementById("tSearch").style.display = "none";
    }
  }




  return (
    <>
      <div className="page-wrapper">
        <div className="teacher-container" id='tForm'>
          <h3 className="text-center">Teacher Entry</h3>
          <button id="cross" className="floating-btn" onClick={() => { closing(true) }}>
            X
          </button>
          <form className="teacher-form" onSubmit={submit}>

            <div className="form-group">
              <label id='nohtingasnkd'>Name</label>
              <input
                id='asndksandsanjdn'
                type="text"
                value={Name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Surname</label>
              <input
                type="text"
                value={Surname}
                onChange={(e) => setSurname(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Last Education</label>
              <input
                type="text"
                value={Edu}
                onChange={(e) => setEdu(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Stream</label>
              <input
                type="text"
                value={Stream}
                onChange={(e) => setStream(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Commission</label>
              <input
                type="number"
                value={Comm}
                onChange={(e) => setComm(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                value={Add}
                onChange={(e) => setAdd(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Aadhaar Number</label>
              <input
                type="text"
                maxLength="12"
                value={Adno}
                onChange={(e) => setAdno(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Reference</label>
              <input
                type="text"
                value={Ref}
                onChange={(e) => setRef(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Mobile Number</label>
              <input
                type="tel"
                maxLength="10"
                value={Mob}
                onChange={(e) => setMob(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Date Of Joining</label>
              <input
                type="date"
              />
            </div>

            <button className="submit-button" type="submit">
              Submit
            </button>
          </form>
        </div>
        <div className="teacher-container" id='tSearch' onLoad={getData}>
          <h3 className='text-center'>Teacher Details</h3>
          <form className="teacher-form">

            <input type="text" placeholder="Name or Surname" />
            <input type="text" placeholder="Stream" />
            <button type="submit">Search</button>
            <button type="button" onClick={() => { closing(false) }}>Add Teacher</button>
          </form>
          <hr />
          <div id="teacherData">
            <table className="teacher-table">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Name</th>
                  <th>Stream</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {teachers.map((t) => (
                  <tr className="rows" key={t.Id}>
                    <td>{t.Id}</td>
                    <td>{t.Name} {t.Surname}</td>
                    <td>{t.Stream}</td>
                    <td>
                      <button
                        className="view-btn"
                        onClick={()=>{closingView(true)}}  // <- use Id here too
                      >
                        View
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <div className="teacher-container" id='tView'>
          <h3 className="text-center">Teacher Entry</h3>
          <button id="cross" className="floating-btn" onClick={()=>{closingView(true)}}>
            X
          </button>
          <form className="teacher-form" onSubmit={submit}>

            <div className="form-group">
              <label id='nohtingasnkd'>Name</label>
              <input
                id='asndksandsanjdn'
                type="text"
                value={Name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Surname</label>
              <input
                type="text"
                value={Surname}
                onChange={(e) => setSurname(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Last Education</label>
              <input
                type="text"
                value={Edu}
                onChange={(e) => setEdu(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Stream</label>
              <input
                type="text"
                value={Stream}
                onChange={(e) => setStream(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Commission</label>
              <input
                type="number"
                value={Comm}
                onChange={(e) => setComm(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                value={Add}
                onChange={(e) => setAdd(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Aadhaar Number</label>
              <input
                type="text"
                maxLength="12"
                value={Adno}
                onChange={(e) => setAdno(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Reference</label>
              <input
                type="text"
                value={Ref}
                onChange={(e) => setRef(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Mobile Number</label>
              <input
                type="tel"
                maxLength="10"
                value={Mob}
                onChange={(e) => setMob(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Date Of Joining</label>
              <input
                type="date"
              />
            </div>

            <button className="submit-button" type="submit">
              Submit
            </button>
          </form>
        </div>
      </div>
    </>
  )
}

export default Student
