import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';

const AddBatch = () => {
    const navigate = useNavigate();
    const [Teachers, setTeachers] = useState([]);
    const [Students, setStudents] = useState([]);
    const [Name, setName] = useState([]);
    const [Course, setCourse] = useState([]);
    const [Teacher, setTeacher] = useState([]);


    // Get Teachers Data
    useEffect(() => {
        const getTeachers = async () => {
            let response = await fetch("http://localhost:5000/teachers");
            response = await response.json();
            // console.log(response);
            setTeachers(response);
        };
        getTeachers();
    }, []);


    async function gettingStudents(Course) {
        console.log(Course);
        const res = await fetch("http://localhost:5000/StudentData");
        const data = await res.json();
        const result = data.filter(e => e.Course === Course);
        // console.log(Course);
        console.log(result)
        setStudents(result);
        // console.log("Fetched students:", data);        // correct
    }


    return (
        <div className="page-wrapper">
            <div className="teacher-container" id='tForm'>
                <h3 className="text-center">Batch Entry</h3>
                <button id="cross" className="floating-btn" onClick={() => { navigate("/Batch") }}>
                    X
                </button>
                <form className="teacher-form">

                    <div className="form-group">
                        <label id="nohtingasnkd">Batch Name</label>
                        <input id="asndksandsanjdn" required value={Name} onChange={(e) => { setName(e.target.value) }} />
                    </div>

                    <div className="form-group">
                        <label>Course</label>
                        <select className="selection1" required value={Course} onChange={(e) => { const selectedCourse = e.target.value; setCourse(selectedCourse);
                            gettingStudents(selectedCourse);
                        }}>
                            <option value="">Select Course</option>
                            <option value="Computer Champs">Computer Champs</option>
                            <option value="Computer Fundamentals">Computer Fundamentals</option>
                            <option value="Advanced Excel">Advanced Excel</option>
                            <option value="Tally Prime + GST">Tally Prime + GST</option>
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="">Teacher Assigned</label>
                        <select name="" id="" className='selection1' required value={Teacher} onChange={(e) => { setTeacher(e.target.value) }}>
                            <option value="">Select Teacher</option>
                            {
                                Teachers.map((t) => (
                                    <option value={t.Name}>{t.Name}</option>
                                ))
                            }
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="">Timing</label>
                        <select name="" id="" className='selection1'>
                            <option value="">Select Timing </option>
                            <option value="10:00 - 12:00">10:00 - 12:00</option>
                            <option value="04:00 - 06:00">04:00 - 06:00</option>
                            <option value="06:00 - 08:00">06:00 - 08:00</option>
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="">Start Data</label>
                        <input type="date" />
                    </div>

                    <div className="form-group">
                        <label htmlFor="">End Data</label>
                        <input type="date" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Add Students</label>
                        <select name="" id="" className='selection1' required >
                            <option value="">Select Student</option>
                            {
                                Students.map((t) => (
                                    <option value={t.Name}>{t.Name}</option>
                                ))
                            }
                        </select>
                        <button className="submit-button" type="button" >Add Student</button>
                    </div>
                    <div className="batch-bottom">
                        <table className="batch-table">
                            <thead>
                                <tr>
                                    <th>No.</th>
                                    <th>Name</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>1</td>
                                    <td>Talha Ansari</td>
                                    <td>
                                        <button type="button" className="remove-btn">Remove</button>
                                    </td>
                                </tr>
                                <tr>
                                    <td>2</td>
                                    <td>Hashi Ansari</td>
                                    <td>
                                        <button type="button" className="remove-btn">Remove</button>
                                    </td>
                                </tr>
                            </tbody>
                        </table>

                        <button className="submit-button" type="button" onClick={() => { gettingStudents() }}>Submit</button>
                    </div>
                </form>
            </div >
        </div >
    )
}

export default AddBatch
