import React, { useState, useEffect } from "react";

let num = 0;

const closing = () => {
  if (num === 0) {
    document.getElementById("tForm").style.display = "block";
    document.getElementById("tSearch").style.display = "none";
    num = 1;
  } else {
    document.getElementById("tForm").style.display = "none";
    document.getElementById("tSearch").style.display = "block";
    num = 0;
  }
};

const Student = () => {
  const [teachers, setTeachers] = useState([]);
  const [Name, setName] = useState("");
  const [Surname, setSurname] = useState("");
  const [Edu, setEdu] = useState("");
  const [Stream, setStream] = useState("");
  const [Comm, setComm] = useState("");
  const [Add, setAdd] = useState("");
  const [Adno, setAdno] = useState("");
  const [Ref, setRef] = useState("");
  const [Mob, setMob] = useState("");

  const submit = async (e) => {
    e.preventDefault();
    const formData = { Name, Surname, Edu, Stream, Comm, Add, Adno, Ref, Mob };

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      console.log(response);
      alert("Form submitted successfully!");
    } catch (err) {
      console.log(err);
      alert("Error submitting form");
    }
  };

  const getData = async () => {
    try {
      const res = await fetch("http://localhost:5000/teachers");
      const data = await res.json();
      console.log("teachers:", data);
      setTeachers(data);
    } catch (error) {
      console.error("Error fetching teachers", error);
    }
  };

  // ✅ fetch only when component mounts
  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <div className="page-wrapper">
        <div className="teacher-container" id="tForm">
          {/* ... your form as is ... */}
        </div>

        {/* ❌ remove onLoad={getData} from div */}
        <div className="teacher-container" id="tSearch">
          <h3 className="text-center">Teacher Details</h3>
          <form className="teacher-form">
            <input type="text" placeholder="Name or Surname" />
            <input type="text" placeholder="Stream" />
            <button type="submit">Search</button>
            <button type="button" onClick={closing}>
              Add Teacher
            </button>
          </form>
          <hr />
          <div id="teacherData">
            {teachers.map((t) => (
              <div className="rows" key={t.id}>
                <p>{t.id}</p>
                <p>
                  {t.Name} {t.Surname}
                </p>
                <p>{t.Stream}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default Student;
