import React, { useState, useEffect } from 'react'
// import { data } from 'react-router-dom';

const Student = () => {
  // let newName, newStream;

  const [teachers, setTeachers] = useState([]);
  const [newName, setnewName] = useState("");
  const [newStream, setnewStream] = useState("");
  const [Name, setName] = useState("");
  const [Surname, setSurname] = useState("");
  const [Edu, setEdu] = useState("");
  const [Gender, setGender] = useState("");
  const [Stream, setStream] = useState("");
  const [Comm, setComm] = useState("");
  const [Add, setAdd] = useState("");
  const [Adno, setAdno] = useState("");
  const [Ref, setRef] = useState("");
  const [Mob, setMob] = useState("");
  const [Date, setDate] = useState("");

  // Submitting The Form

  const submit = async (e) => {
    e.preventDefault();

    const formData = {
      Name: Name.trim(),
      Surname: Surname.trim(),
      Edu: Edu.trim(),
      Stream: Stream.trim(),
      Comm: Number(Comm),
      Adno: Number(Adno),
      Mob: Number(Mob),
      Add: Add.trim(),
      Ref: Ref.trim(),
      Date
    };


    const hasEmptyField = Object.values(formData).some(
      value => value === "" || value === 0 || Number.isNaN(value)
    );

    if (hasEmptyField) {
      alert("All fields are required");
      return;
    }

    if (String(Mob).length !== 10) {
      alert("Mobile number must be 10 digits");
      return;
    }

    if (String(Adno).length !== 12) {
      alert("Aadhaar number must be 12 digits");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        throw new Error("Failed to submit");
      }

      alert("Form submitted successfully!");
    } catch (err) {
      console.error(err);
      alert("Error submitting form");
    }
  };


  // Getting Data from Database
  // let teachers;
  const getData = async () => {
    try {
      const res = await fetch("http://localhost:5000/teachers");
      const data = await res.json();
      setTeachers(data);
    } catch (error) {
      console.error("Error fetching teachers", error.status);
    }
  }

  // Displaying Data in View
  const closingView = (opt, id) => {
    if (opt === true) {
      document.getElementById("tView").style.display = "none";
      document.getElementById("tSearch").style.display = "block";
    } else {
      document.getElementById("tView").style.display = "block";
      document.getElementById("tSearch").style.display = "none";
      // alert("I am Clicked with ID : " + id)
      // setName(t.Name)
      const result = teachers.filter(e => e.Id === id);
      console.log(result[0]["Edu"]);
      console.log(result[0]["Add"]);
      console.log(result[0]["Ref"]);
      console.log(result[0]["Mob"]);

      setName(result[0]["Name"]);
      setSurname(result[0]["Surname"]);
      setEdu(result[0]["Education"]);
      setStream(result[0]["Stream"]);
      setComm(result[0]["Comm"]);
      setAdd(result[0]["Address"]);
      setAdno(result[0]["Adno"]);
      setRef(result[0]["Reference"]);
      setMob(result[0]["Mobile_number"]);
      // console.log(result[0]["Id"]);
    }
  }

  const closing = (opt) => {
    // alert("I am Clicked");
    if (opt === false) {
      document.getElementById("tForm").style.display = "block";
      document.getElementById("tSearch").style.display = "none";
    } else {
      document.getElementById("tForm").style.display = "none";
      document.getElementById("tSearch").style.display = "block";
    }
    setName("");
    setSurname("");
    setEdu("");
    setStream("");
    setComm("");
    setAdd("");
    setAdno("");
    setRef("");
    setMob("");

  }

  // Searching in the Data

  let searching = (newName, newStream) => {
    // e.preventDefault();
    const result = teachers.filter(e => e.Name === "Hashir " &&
      e.Surname === "Ansari" && e.Stream === "Non IT");
    console.log(result);

  }

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <div className="page-wrapper">
        <div className="teacher-container" id='tForm'>
          <h3 className="text-center">Teacher Entry</h3>
          <button id="cross" className="floating-btn" onClick={() => { closing(true) }}>
            X
          </button>
          <form className="teacher-form" onSubmit={submit}>

            <div className="form-group">
              <label id='nohtingasnkd'>Name</label>
              <input
                id='asndksandsanjdn'
                type="text"
                value={Name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Surname</label>
              <input
                type="text"
                value={Surname}
                onChange={(e) => setSurname(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Last Education</label>
              <input
                type="text"
                value={Edu}
                onChange={(e) => setEdu(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Commission</label>
              <input
                type="number"
                value={Comm}
                onChange={(e) => setComm(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                value={Add}
                onChange={(e) => setAdd(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Aadhaar Number</label>
              <input
                type="text"
                maxLength="12"
                value={Adno}
                onChange={(e) => setAdno(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Reference</label>
              <input
                type="text"
                value={Ref}
                onChange={(e) => setRef(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Mobile Number</label>
              <input
                type="tel"
                maxLength="10"
                value={Mob}
                onChange={(e) => setMob(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Date Of Joining</label>
              <input
                type="date"
                value={Date}
                onChange={(e) => setDate(e.target.value)}
              />
            </div>

            <div className="form-group form-group-row">
              <div className="field">
                <label>Stream</label>
                <select
                  id="selection1"
                  value={Stream}
                  onChange={(e) => setStream(e.target.value)}
                >
                  <option value="IT">IT</option>
                  <option value="Non IT">Non IT</option>
                </select>
              </div>

              <div className="field">
                <label>Gender</label>
                <select
                  id="gender"
                  value={Gender}
                  onChange={(e) => setGender(e.target.value)}
                >
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                </select>
              </div>
            </div>


            <button className="submit-button" type="submit">
              Submit
            </button>
          </form>
        </div>
        <div className="teacher-container" id='tSearch'>
          <h3 className='text-center'>Teacher Details</h3>
          <form className="teacher-form">

            <input type="text" placeholder="Name or Surname" onChange={(e) => { setnewName(e.target.value) }} />
            <select name="" id="selection" value={newStream} onChange={(e) => { setnewStream(e.target.value) }}>
              <option value="IT">IT</option>
              <option value="Non IT">Non IT</option>
            </select>
            <button type="button" onClick={() => searching(newName, newStream)}>Search</button>
            <button type="button" onClick={() => { closing(false) }}>Add Teacher</button>
          </form>
          <hr />
          <div id="teacherData">
            <table className="teacher-table">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Name</th>
                  <th>Stream</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {teachers.map((t) => (
                  <tr className="rows" key={t.Id}>
                    <td>{t.Id}</td>
                    <td>{t.Name} {t.Surname}</td>
                    <td>{t.Stream}</td>
                    <td>
                      <button
                        className="view-btn"
                        onClick={() => { closingView(false, t.Id) }}  // <- use Id here too
                      >
                        View
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <div className="teacher-container" id='tView'>
          <h3 className="text-center">Teacher Entry</h3>
          <button id="cross" className="floating-btn" onClick={() => { closingView(true) }}>
            X
          </button>
          <form className="teacher-form" onSubmit={submit}>

            <div className="form-group">
              <label id='nohtingasnkd'>Name</label>
              <input
                id='asndksandsanjdn'
                type="text"
                value={Name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Surname</label>
              <input
                type="text"
                value={Surname}
                onChange={(e) => setSurname(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Last Education</label>
              <input
                type="text"
                value={Edu}
                onChange={(e) => setEdu(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Stream</label>
              <input
                type="text"
                value={Stream}
                onChange={(e) => setStream(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Commission</label>
              <input
                type="number"
                value={Comm}
                onChange={(e) => setComm(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                value={Add}
                onChange={(e) => setAdd(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Aadhaar Number</label>
              <input
                type="text"
                maxLength="12"
                value={Adno}
                onChange={(e) => setAdno(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Reference</label>
              <input
                type="text"
                value={Ref}
                onChange={(e) => setRef(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Mobile Number</label>
              <input
                type="tel"
                maxLength="10"
                value={Mob}
                onChange={(e) => setMob(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>Date Of Joining</label>
              <input
                type="date"
              />
            </div>

            <button className="submit-button" type="submit">
              Submit
            </button>
          </form>
        </div>
      </div>
    </>
  )
}

export default Student
