import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom";

// import { data } from 'react-router-dom';

const Student = () => {
  // let newName, newStream;
  const navigate = useNavigate();

  const [teachers, setTeachers] = useState([]);
  const [newName, setnewName] = useState("");
  const [newStream, setnewStream] = useState("");

  // Getting Data from Database
  const getData = async () => {
    try {
      const res = await fetch("http://localhost:5000/teachers");
      const data = await res.json();
      setTeachers(data);
    } catch (error) {
      console.error("Error fetching teachers", error.status);
    }
  }

  // Displaying Data in View
  // asyn function (opt, id) {
  const closingView = (opt, id) => {
    if (opt === true) {
      console.log("Nothing");
    } else {
      // alert("I am Clicked with ID : " + id)
      // setName(t.Name)
      const result = teachers.filter(e => e.Id === id);

      const joining = result[0]["Joining_Date"]
        ? new Date(result[0]["Joining_Date"]).toISOString().slice(0, 10)
        : "";
      // console.log(result[0]["Gender"]);

      setName(result[0]["Name"]);
      setSurname(result[0]["Surname"]);
      setEdu(result[0]["Education"]);
      setAdd(result[0]["Address"]);
      setAdno(result[0]["Adno"]);
      setComm(result[0]["Comm"]);
      setAge(result[0]["Age"]);
      setRef(result[0]["Reference"]);
      setMob(result[0]["Mobile_number"]);
      setJoinDate(joining);
      setStream(result[0]["Stream"]);
      setGender(result[0]["Gender"]);

      // console.log(result[0]["Id"]);
    }
  }

  const closing = () => {
    // just clear filters if you want
    setName("");
    setSurname("");
    setEdu("");
    setStream("");
    setComm("");
    setAdd("");
    setAdno("");
    setRef("");
    setMob("");
    setJoinDate("");
    setGender("");
    setAge("");
  };

  // Searching in the Data

  let searching = (newName, newStream) => {
    // e.preventDefault();
    console.log(newName, newStream);
    const result = teachers.filter(e => (e.Name === newName ||
      e.Surname === newName) && e.Stream === newStream);
    console.log(result);
    document.getElementById("")
    return (
      <div className="teacherData" id='Date_2'>
        <table className="teacher-table">
          <thead>
            <tr>
              <th>Id</th>
              <th>Name</th>
              <th>Stream</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {result.map((t, index) => (
              <tr className="rows" key={t.Id}>
                <td>{index + 1}</td>
                <td>{t.Name} {t.Surname}</td>
                <td>{t.Stream}</td>
                <td>
                  <button
                    className="view-btn"
                    onClick={() => { closingView(false, t.Id) }}  // <- use Id here too
                  >
                    View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )
  }

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <div className="page-wrapper">
        <div className="teacher-container" id='tSearch'>
          <h3 className='text-center'>Teacher Details</h3>
          <form className="teacher-form">

            <input type="text" placeholder="Name or Surname" onChange={(e) => { setnewName(e.target.value) }} />
            <select name="" id="selection" value={newStream} onChange={(e) => { setnewStream(e.target.value) }}>
              <option value="">Select Stream</option>
              <option value="IT">IT</option>
              <option value="Non IT">Non IT</option>
            </select>
            <button type="button" onClick={() => searching(newName, newStream)}>Search</button>
            <button type="button" onClick={() => { closing(); navigate("/TeacherForm");}}>Add Teacher</button>
          </form>
          <hr />
          <div className="teacherData" id='Date_1'>
            <table className="teacher-table">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Name</th>
                  <th>Stream</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {teachers.map((t, index) => (
                  <tr className="rows" key={t.Id}>
                    <td>{index + 1}</td>
                    <td>{t.Name} {t.Surname}</td>
                    <td>{t.Stream}</td>
                    <td>
                      <button
                        className="view-btn"
                        onClick={() => { closingView(false, t.Id) }}  // <- use Id here too
                      >
                        View
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        
      </div>
    </>
  )
}

export default Student
