import { Component } from "react";

export default class PropsDemo extends Component {
    constructor(props) {
        super(props);
        this.state = { Subject: "Java" }
    }

    handleChange = (event) => { this.setState({Subject : event.target.value}) }

    render() {
        return <>
            <h3>Enter Your Fav. Language : </h3>
            <input type="text" value={this.state.Subject} onChange={this.handleChange} />
            <h1>Your Fav. Subject - {this.state.Subject}</h1>
            {/* <button onClick={this.handleChange}>Click Me</button> */}
        </>
    }

}
