import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom";

// import TeacherForm from './TeacherForm';

// import TeacherView from "./Components/TeacherView";


// import { data } from 'react-router-dom';

const Teacher = () => {
    // let newName, newStream;
    let navigate = useNavigate();


    const [teachers, setTeachers] = useState([]);
    const [filteredTeachers, setFilteredTeachers] = useState([]);
    const [newName, setnewName] = useState("");
    const [newStream, setnewStream] = useState("");
    const [newRef, setnewRef] = useState("");
    const [newGender, setnewGender] = useState("");

    // Getting Data from Database
    const getData = async () => {
        try {
            const res = await fetch("http://localhost:5000/teachers");
            const data = await res.json();
            setTeachers(data);

        } catch (error) {
            console.error("Error fetching teachers", error.status);
        }
    }

    useEffect(() => {
        getData();
    }, []);


    // Searching in the Data

    const searching = (e, newName, newStream, newRef, newGender) => {
        e.preventDefault();

        if (!newName && !newStream && !newRef && !newGender) {
            alert("Enter something to search...!!!");
            return;
        }

        // Default undefined to empty strings
        const nameLower = (newName || "").trim().toLowerCase();
        const streamLower = (newStream || "").trim().toLowerCase();
        const refLower = (newRef || "").trim().toLowerCase();
        const genderLower = (newGender || "").trim().toLowerCase();

        const result = teachers.filter(t => {
            const matchesName =
                !nameLower ||
                t.Name.toLowerCase() === nameLower ||
                t.Surname.toLowerCase() === nameLower;

            const matchesStream =
                !streamLower || t.Stream.toLowerCase() === streamLower;

            const matchesRef =
                !refLower || t.Reference.toLowerCase() === refLower;

            const matchesGender =
                !genderLower || t.Gender.toLowerCase() === genderLower;

            return matchesName && matchesStream && matchesRef && matchesGender;
        });

        setFilteredTeachers(result);
    };


    useEffect(() => {
        setFilteredTeachers(teachers);
    }, [teachers]);

    const clear = () => {
        setFilteredTeachers(teachers);
        setnewName();
        setnewStream();
        setnewRef();
        setnewGender();
    }

    return (
        <>
            <div className="page-wrapper">
                <div className="teacher-container" id='tSearch'>
                    <h3 className='text-center'>Student Records</h3>
                    {/* Form Here */}
                    <form className="teacher-form teacher-search-form">
                        <input type="text" placeholder="Name or Surname" onChange={(e) => { setnewName(e.target.value) }} />
                        <select name="" id="selection" value={newStream} onChange={(e) => { setnewStream(e.target.value) }}>
                            <option value="">Select Stream</option>
                            <option value="IT">IT</option>
                            <option value="Non IT">Non IT</option>
                        </select>
                        <select name="" value={newRef} onChange={(e) => { setnewRef(e.target.value) }}>
                            <option value="">Select Reference</option>
                            <option value="Family">Family</option>
                            <option value="Friends">Friends</option>
                            <option value="Banner">Banner</option>
                            <option value="Others">Others</option>
                        </select>
                        <select name="" value={newGender} onChange={(e) => { setnewGender(e.target.value) }}>
                            <option value="">Select Gender</option>
                            <option value="Male">Male</option>
                            <option value="Female">Female</option>
                        </select>
                        <button type="button" onClick={(e) => searching(e, newName, newStream, newRef, newGender)}>Search</button>
                        <button type="button" onClick={() => { clear() }}>Clear</button>
                        <button type="button" onClick={() => navigate("/StudentForm")}>Add Student</button>
                    </form>
                    <hr />
                    {/* Form Data */}
                    <div className="teacherData" id='Date_1'>
                        <table className="teacher-table">
                            <thead>
                                <tr>
                                    <th>No.</th>
                                    <th>Name</th>
                                    <th>Stream</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredTeachers.map((t, index) => (
                                    <tr className="rows" key={t.Id}>
                                        <td>{index + 1}</td>
                                        <td>{t.Name} {t.Surname}</td>
                                        <td>{t.Stream}</td>
                                        <td><button className="view-btn" onClick={() => navigate("/StudentView", { state: { id: t.Id } })}> View</button></td>
                                    </tr>
                                ))}
                            </tbody>

                        </table>
                    </div>
                </div>
            </div >
        </>
    )
}

export default Teacher
