import { React, useState, useEffect } from 'react'
import { useNavigate, useLocation } from "react-router-dom";


const TeacherView = () => {

    const location = useLocation();
    const teacherId = location.state?.id; // likely a number or string
    const navigate = useNavigate();

    const [teachers, setTeachers] = useState([]);

    const [Name, setName] = useState("");
    const [Surname, setSurname] = useState("");
    const [Edu, setEdu] = useState("");
    const [Gender, setGender] = useState("Male");
    const [Stream, setStream] = useState("");
    const [Comm, setComm] = useState("");
    const [Age, setAge] = useState("");
    const [Add, setAdd] = useState("");
    const [Adno, setAdno] = useState("");
    const [Ref, setRef] = useState("");
    const [Mob, setMob] = useState("");
    const [JoinDate, setJoinDate] = useState("");

    // teachers = 0;
    for (let index = 0; index < 1; index++) {
        console.log(teachers);
    }

    useEffect(() => {
        const load = async () => {
            try {
                const res = await fetch("http://localhost:5000/teachers");
                const data = await res.json();
                setTeachers(data);

                // ensure types match: convert both to numbers
                const idNum = Number(teacherId);
                const teacher = data.find(t => Number(t.Id) === idNum);

                if (!teacher) {
                    console.warn("Teacher not found for id:", teacherId);
                    return;
                }

                const joining = teacher.Joining_Date
                    ? new Date(teacher.Joining_Date).toISOString().slice(0, 10)
                    : "";

                setName(teacher.Name);
                setSurname(teacher.Surname);
                setEdu(teacher.Education);
                setAdd(teacher.Address);
                setAdno(teacher.Adno);
                setComm(teacher.Comm);
                setAge(teacher.Age);
                setRef(teacher.Reference);
                setMob(teacher.Mobile_number);
                setJoinDate(joining);
                setStream(teacher.Stream);
                setGender(teacher.Gender);
            } catch (err) {
                console.error("Error fetching teachers", err);
            }
        };

        if (teacherId != null) {
            load();
        }
    }, [teacherId]);

    const save = () => {
        console.log("Saved")
    }

    return (
        <>
            <div className="page-wrapper">
                <div className="teacher-container" id='tView'>
                    <h3 className="text-center">Teacher Detail</h3>
                    <button id="cross" className="floating-btn" onClick={() => { navigate("/Teacher") }}>X</button>
                    <form className="teacher-form">

                        <div className="form-group">
                            <label id='nohtingasnkd'>Name</label>
                            <input id='asndksandsanjdn' type="text" value={Name} onChange={(e) => setName(e.target.value)} required />
                        </div>

                        <div className="form-group">
                            <label>Surname</label>
                            <input type="text" value={Surname} onChange={(e) => setSurname(e.target.value)} required />
                        </div>


                        <div className="form-group">
                            <label>Last Education</label>
                            <input type="text" value={Edu} onChange={(e) => setEdu(e.target.value)} />
                        </div>


                        <div className="form-group">
                            <label>Address</label>
                            <input type="text" value={Add} onChange={(e) => setAdd(e.target.value)} />
                        </div>

                        <div className="form-group">
                            <label>Aadhaar Number</label>
                            <input type="text" maxLength="12" value={Adno} onChange={(e) => setAdno(e.target.value)} />
                        </div>

                        <div className="form-group form-group-row2">
                            <div className="field">
                                <label>Commission</label>
                                <input type="number" value={Comm} onChange={(e) => setComm(e.target.value)} />
                            </div>

                            <div className="field">
                                <label>Age</label>
                                <input type="number" value={Age} onChange={(e) => setAge(e.target.value)} />
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Reference</label>
                            <input type="text" value={Ref} onChange={(e) => setRef(e.target.value)} />
                        </div>


                        <div className="form-group">
                            <label>Mobile Number</label>
                            <input type="tel" maxLength="10" value={Mob} onChange={(e) => setMob(e.target.value)} />
                        </div>


                        <div className="form-group">
                            <label>Date Of Joining</label>
                            <input type="date" value={JoinDate} onChange={(e) => setJoinDate(e.target.value)} />
                        </div>

                        <div className="form-group form-group-row">
                            <div className="field">
                                <label>Stream</label>
                                <select id="selection1" value={Stream} onChange={(e) => setStream(e.target.value)}>
                                    <option value="IT">IT</option>
                                    <option value="Non IT">Non IT</option>
                                </select>
                            </div>

                            <div className="field">
                                <label>Gender</label>
                                <select id="gender" value={Gender} onChange={(e) => setGender(e.target.value)} >
                                    <option value="Male">Male</option>
                                    <option value="Female">Female</option>
                                </select>
                            </div>
                        </div>

                        <button className="submit-button" type="submit" onClick={() => { save() }}>
                            Save
                        </button>
                    </form>
                </div>
            </div>
        </>
    )
}

export default TeacherView
