import React, { useState } from 'react'

const closing = () => {
  // alert("I am Clicked");
  document.getElementById("tForm").style.display = "none";
  document.getElementById("tSearch").style.display = "block";
}


const Student = () => {
  const submit = async (e) => {
    e.preventDefault();
    // alert("Hi " + Name + Surname + Edu + Stream + Comm + Add + Adno + Ref + Mob)

    const formData = {
      Name,
      Surname,
      Edu,
      Stream,
      Comm,
      Add,
      Adno,
      Ref,
      Mob
    };

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      });
      const data = await response.JSON();
      console.log("Server response : ", data);
      alert("Form submitted successfully!");
    }
    catch (err) {
      console.log(err)
      alert("Error submitting form");
    }

  }

  const [Name, setName] = useState("");
  const [Surname, setSurname] = useState("");
  const [Edu, setEdu] = useState("");
  const [Stream, setStream] = useState("");
  const [Comm, setComm] = useState("");
  const [Add, setAdd] = useState("");
  const [Adno, setAdno] = useState("");
  const [Ref, setRef] = useState("");
  const [Mob, setMob] = useState("");

  return (
    <>
      <div className="page-wrapper" id="tForm">
        <div className="teacher-container">
          <h3 className="title">Teacher Entry</h3>
          <button id='cross' className='floating-btn' onClick={closing}>X</button>

          <form className="teacher-form" onSubmit={submit}>
            <div className="form-group frt">
              <label>Name</label>
              <input type="text" value={Name} onChange={(e) => { setName(e.target.value) }} />
            </div>

            <div className="form-group">
              <label>Surname</label>
              <input type="text" value={Surname} onChange={(e) => { setSurname(e.target.value) }} />
            </div>

            <div className="form-group">
              <label>Last Education</label>
              <input type="text" value={Edu} onChange={(e) => { setEdu(e.target.value) }} />
            </div>


            <div className="form-group">
              <label>Stream</label>
              <input type="text" value={Stream} onChange={(e) => { setStream(e.target.value) }} />
            </div>

            <div className="form-group">
              <label>Commission</label>
              <input type="number" value={Comm} onChange={(e) => { setComm(e.target.value) }} />
            </div>

            <div className="form-group">
              <label>Address</label>
              <input type="text" value={Add} onChange={(e) => { setAdd(e.target.value) }} />
            </div>

            <div className="form-group">
              <label>Adhaar Number</label>
              <input type="number" value={Adno} onChange={(e) => { setAdno(e.target.value) }} />
            </div>

            <div className="form-group">
              <label>Reference</label>
              <input type="text" value={Ref} onChange={(e) => { setRef(e.target.value) }} />
            </div>

            <div className="form-group">
              <label>Mobile Number</label>
              <input type="number" value={Mob} onChange={(e) => { setMob(e.target.value) }} />
            </div>
            <div className="form-group">
              <label>Date Of Joining</label>
              <input type="date" />
            </div>
            <button class="submit-button">Submit</button>
          </form>
        </div>
      </div>
      <div className="teacher-container" id='tSearch'>
        <h3>Teacher Info</h3>
      </div>
    </>
  )
}

export default Student
