import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom";

// import TeacherForm from './TeacherForm';

// import TeacherView from "./Components/TeacherView";


// import { data } from 'react-router-dom';

const Teacher = () => {
  // let newName, newStream;
  let navigate = useNavigate();


  const [teachers, setTeachers] = useState([]);
  const [filteredTeachers, setFilteredTeachers] = useState([]);
  const [newName, setnewName] = useState("");
  const [newStream, setnewStream] = useState("");
  const [newRef, setnewRef] = useState("");

  // Getting Data from Database
  const getData = async () => {
    try {
      const res = await fetch("http://localhost:5000/teachers");
      const data = await res.json();
      setTeachers(data);

    } catch (error) {
      console.error("Error fetching teachers", error.status);
    }
  }

  useEffect(() => {
    getData();
  }, []);


  // Searching in the Data

  const searching = (e, newName, newStream, newRef) => {
    e.preventDefault();
    let result = [];
    if (newName === "" && newStream === "" && newRef === "") {
      alert("Enter Something to Search...!!!");
      return;
    }
    if (newName !== "" && newStream !== "" && newStream !== "") {
      result = teachers.filter(
        t =>
          (t.Name === newName || t.Surname === newName) &&
          t.Stream === newStream && t.Reference === newRef
      );
    }
    // Name - Stream
    else if (newName !== "" && newStream !== "") {
      result = teachers.filter(
        t =>
          (t.Name === newName || t.Surname === newName) &&
          t.Stream === newStream);
    }
    // Name - Ref
    else if (newName !== "" && newRef !== "") {
      result = teachers.filter(
        t =>
          (t.Name === newName || t.Surname === newName) &&
          t.Reference === newRef);
    }
    // Stream - Ref
    else if (newStream !== "" && newRef !== "") {
      result = teachers.filter(
        t =>
          t.Stream === newStream &&
          t.Reference === newRef);
    }

    //Only Name is present
    else if (newStream === "" && newRef === "") {
      result = teachers.filter(
        t => t.Name === newName || t.Surname === newName
      );
    }

    //Only Stream is present
    else if (newName === "" && newRef === "") {
      result = teachers.filter(t => t.Stream === newStream);
    }

    //Only Ref is present
    else if (newName === "" && newStream === "") {
      result = teachers.filter(t => t.Reference === newRef);
    }

    else if (newName === "" && newStream === "") {
      result = teachers.filter(t => t.Reference === newRef);
    }
    setFilteredTeachers(result); // ✅ THIS triggers re-render
  };

  useEffect(() => {
    setFilteredTeachers(teachers);
  }, [teachers]);

  const clear = () => {
    setFilteredTeachers(teachers);
  }

  return (
    <>
      <div className="page-wrapper">
        <div className="teacher-container" id='tSearch'>
          <h3 className='text-center'>Teacher Details</h3>
          {/* Form Here */}
          <form className="teacher-form">
            <input type="text" placeholder="Name or Surname" onChange={(e) => { setnewName(e.target.value) }} />
            <select name="" id="selection" value={newStream} onChange={(e) => { setnewStream(e.target.value) }}>
              <option value="">Select Stream</option>
              <option value="IT">IT</option>
              <option value="Non IT">Non IT</option>
            </select>
            <select name="" value={newRef} onChange={(e) => { setnewRef(e.target.value) }}>
              <option value="">Select Reference</option>
              <option value="Family">Family</option>
              <option value="Friends">Friends</option>
              <option value="Banner">Banner</option>
              <option value="Others">Others</option>
            </select>
            <button type="button" onClick={(e) => searching(e, newName, newStream, newRef)}>Search</button>
            <button type="button" onClick={() => { clear() }}>Clear</button>
            <button type="button" onClick={() => navigate("/TeacherForm")}>Add Teacher</button>
          </form>
          <hr />
          {/* Form Data */}
          <div className="teacherData" id='Date_1'>
            <table className="teacher-table">
              <thead>
                <tr>
                  <th>No.</th>
                  <th>Name</th>
                  <th>Stream</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {filteredTeachers.map((t, index) => (
                  <tr className="rows" key={t.Id}>
                    <td>{index + 1}</td>
                    <td>{t.Name} {t.Surname}</td>
                    <td>{t.Stream}</td>
                    <td>
                      <button
                        className="view-btn"
                        onClick={() =>
                          navigate("/TeacherView", { state: { id: t.Id } })
                        }
                      >
                        View
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>

            </table>
          </div>
        </div>
      </div >
    </>
  )
}

export default Teacher
